mapper.select:
select id,title,author,content,keywords,sort,brief, date_format(publishtime,'%Y-%m-%d %H:%i:%s') as publishtime, date_format(shelftime,'%Y-%m-%d %H:%i:%s') as shelftime, istop,date_format(createtime,'%Y-%m-%d %H:%i:%s') as createtime, `type`,url from advice where 1=1
and title like concat('%',#{title},'%')
and author like concat('%',#{author},'%')
and keywords like concat('%',#{keywords},'%')
and istop = #{istop}
order by istop desc,createtime desc limit ${offset},${limit}
