mapper.select:
select id, role_name,status,remark
from system_role
where 1=1

    and `status` = #{status}


    and `role_name` like concat('%',#{roleName},'%')


    limit ${offset},${limit}

select count(1) from system_role where 1=1
and `status` = #{status}
and `role_name` like concat('%',#{roleName},'%')

mapper.update:
update system_role
set role_name = #{roleName},
remark=#{remark},
`status`=#{status}
where id = #{roleid}
